create table contracts (
  contract_id text primary key,
  parent text references contracts (contract_id),
  date_sign date not null,
  date_pub date,
  date_start date not null,
  date_end date,
  company_name text not null,
  title text not null,
  description text not null,
  url text not null
);

insert into contracts values ('CT00452019', null, '2019-07-11', '2019-07-15', '2019-07-11', '2020-07-10', 'Adtel Tecnologia', 'Marcenaria e serralheria', 'Contratação de empresa para a prestação de serviços contínuos e sob demanda referentes à manutenção preventiva, corretiva e preditiva dos sistemas e equipamentos de marcenaria, serralheria e obras civis do Complexo Arquitetônico do Senado Federal (inclusive Residências Oficiais) e áreas comuns do Congresso Nacional, com a disponibilização de mão de obra qualificada e com suprimento de insumos necessários à execução dos serviços.', 'https://www6g.senado.gov.br/transparencia/licitacoes-e-contratos/contratos/4723');
insert into contracts values ('CT00662019', null, '2019-09-25', '2019-09-26', '2019-09-25', '2020-09-24', 'JR Comércio e Vidros Ltda.', 'Vidros', 'Contratação de empresa especializada para o fornecimento, instalação, remanejamento, remoção e transporte de painéis de vidros temperados, vidros laminados e vidros comuns, incluindo portas, esquadrias, painéis, espelhos, baguetes e ferragens, assim como a prestação de serviços de manutenção de vidros comuns, com substituição de massa e silicone; de portas, esquadrias e painéis de vidros temperados, à medida que houver necessidade, no Complexo Arquitetônico e nas Residências Oficiais do SENADO FEDERAL, durante o período de 12 (doze) meses consecutivos.', 'https://www6g.senado.gov.br/transparencia/licitacoes-e-contratos/contratos/4751');
insert into contracts values ('CT00812017', null, '2017-10-19', '2017-10-25', '2017-10-19', '2020-10-18', 'RCS Tecnologia Ltda.', 'Manutenção civil', 'Contratação de empresa especializada para a prestação de serviços continuados de manutenção preventiva e corretiva dos sistemas de revestimentos, vedações, forros, pinturas e pavimentação viária no Complexo Arquitetônico do SENADO FEDERAL, durante o período de 36 (trinta e seis) meses consecutivos.', 'https://www6g.senado.gov.br/transparencia/licitacoes-e-contratos/contratos/4213');
